import { createReducer, on } from '@ngrx/store';
import { crear } from './todo.actions';
import { Todo } from '../models/todo.model';
export const initialState = [new Todo('salvar el mundo'), new Todo('comprar palomitas'), new Todo('mejor robar palomitas'), new Todo('que no te pillen', true)];
export const todoReducer = createReducer(initialState, on(crear, (state, {
  texto
}) => [...state, new Todo(texto)]));
export const changeToggle = createReducer(initialState, on(crear, (state, {
  id
}) => {
  return state.map(todo => {
    return {
      ...todo,
      completado: !todo.completado
    };
  });
}));
